var http=require("http");
//创建一个服务器   请求request，，返回respons
// http.createServer(function(req,res){
//     res.end("1");
// }).listen(8080);//端口号 ，1024以上
var fs=require("fs");
http.createServer(function(req,res){
    var url="."+req.url;
    fs.readFile(url,function(e,data){
        console.log(e);
        if(e){
            res.write("网页走丢了！");
        }else{
            // res.writeHead(200,{'Content-type':'text/html'});
            res.write(data);
        }
        res.end();
    })
}).listen(8080);
